select q'[select ']' || owner || '.' || table_name || q'[' tble, count(*) cnt from ]' || owner || '.' || table_name || q'[ where transaction_desc like 'Change%' and upper(transaction_desc) NOT LIKE '% FROM %' union all]' line
  from all_tables
 where table_name like '%AUDIT';

