select q'[select ']' || owner || '.' || table_name || '.' || column_name || q'[' tble, count(*) cnt from ]' || owner || '.' || table_name || q'[ where ]' || column_name || q'[ != upper(]' || column_name || q'[) union all]' line
  from all_tab_columns
 where     owner = 'LHHCOM'
       and data_type like '%CHAR%'
       and table_name not like '%AUDIT'
       and table_name not like '%TEMP%'
       and table_name not like '%XX%'
       and table_name not like '%SOAP%'
       and table_name not like '%UPLD%'
       and table_name not like '%TMP%'
       and table_name not like '%TEMP%'
       and table_name not like '%BCK%'
       and table_name not like '%TEST%'
       and table_name not like '%LOGGER%'
       and table_name not like '%EXT%';

